mapper.delete:
DELETE FROM gk_subject


        AND id = #{id}


        AND subjectName = #{subjectName}


        AND status = #{status}


        AND creator = #{creator}


        AND createDate = #{createDate}


        AND lastModDate = #{lastModDate}


        AND lastModifier = #{lastModifier}


        AND areaId = #{areaId}

