mapper.select:
select b.majoredId as code,b.majoredName as name,b.enrollNumber as planNumber,
 b.schoolLength,b.foreignLanguage as foreginLanguage,b.feeStandard,b.universityCode,b.universityName

 from gk_majored a  left join gk_enroll_plan  b  on a.id=b.majoredId  where 1=1 and b.year='2015'


     and a.subjectType=#{condition.subjectTypeName}




    and a.majoredType=#{condition.majoredTypeName}



    and b.majoredName like CONCAT('%',#{condition.searchName},'%')


     and b.batch=#{condition.batchTypeName}

 limit #{condition.pageNo},#{condition.pageSize}

select count(*)
from gk_majored a  left join gk_enroll_plan  b  on a.id=b.majoredId  where 1=1 and b.year='2015'


    and a.subjectType =#{condition.subjectTypeName}




    and a.majoredType=#{condition.majoredTypeName}



    and b.majoredName like CONCAT('%',#{condition.searchName},'%')


    and b.batch=#{condition.batchTypeName}

    SELECT majoredId id,majoredName name,subject,schoolLength,feeStandard,enrollNumber planNum
FROM gk_enroll_plan

    1=1

        and universityCode = #{code}


        and batch = #{batch}


        and year = #{year}
