mapper.select:
SELECT a.code,a.name,a.subjection,a.type,b.lgPlanNumber planNum,b.lgEnrollNumber enrollNum,
b.lgLowestScore lowestScore ,b.lgLowestRank lowestRanking,b.lgAverageScore averageScore,b.lgAverageRank averageScoresRanking
,c.name dictName
FROM gk_university a
left join gk_enroll_report b on a.code = b.universityCode
left join gk_university_dict c on c.dictId = a.property

    1=1

        and b.year = #{year}


        and a.code =#{code}


        and b.batch = #{batch}

