SELECT id,createDate,hotInformation,informationContent FROM gk_gkinformation_gkhot
WHERE  status<>  -1 ORDER BY createDate ASC
limit ${offset}, ${rows}

SELECT id,createDate,hotInformation,informationContent FROM gk_gkinformation_gkhot
WHERE status = 0 AND hotInformation LIKE CONCAT('%', #{key} , '%')  ORDER BY createDate ASC
limit ${offset}, ${rows}
