mapper.select:
SELECT id,createDate,lastModDate,hotInformation,informationSubContent FROM gk_gkinformation_gkhot
WHERE  status<>  -1 ORDER BY createDate DESC
limit ${offset}, ${rows}

SELECT id,createDate,lastModDate,hotInformation,informationSubContent FROM gk_gkinformation_gkhot
WHERE status = 0 AND hotInformation LIKE CONCAT('%', #{key} , '%')  ORDER BY createDate DESC
limit ${offset}, ${rows}

SELECT id,createDate,lastModDate,hotInformation,informationContent FROM gk_gkinformation_gkhot
WHERE status = 0 ORDER BY hotCount DESC
limit ${offset},${rows}

mapper.update:
UPDATE gk_gkinformation_gkhot
SET hotCount = hotCount + 1
WHERE id=#{id}
