mapper.select:
SELECT
id,
categoryId,
title,
summary,
sort,
status,
creator,
createDate,
lastModifier,
lastModDate,
hits,
areaId
FROM gk_volunteer_school



        and id in (#{condition.whereSql})



        ${condition.groupOp}   id  ${condition.id.op}  #{condition.id.data}


        ${condition.groupOp}   categoryId  ${condition.categoryId.op}  #{condition.categoryId.data}


        ${condition.groupOp}   title  ${condition.title.op}  #{condition.title.data}


        ${condition.groupOp}   summary  ${condition.summary.op}  #{condition.summary.data}


        ${condition.groupOp}   content  ${condition.content.op}  #{condition.content.data}


        ${condition.groupOp}   sort  ${condition.sort.op}  #{condition.sort.data}


        ${condition.groupOp}   status  ${condition.status.op}  #{condition.status.data}


        ${condition.groupOp}   creator  ${condition.creator.op}  #{condition.creator.data}


        ${condition.groupOp}   createDate  ${condition.createDate.op}  #{condition.createDate.data}


        ${condition.groupOp}   lastModifier  ${condition.lastModifier.op}  #{condition.lastModifier.data}


        ${condition.groupOp}   lastModDate  ${condition.lastModDate.op}  #{condition.lastModDate.data}


        ${condition.groupOp}   hits  ${condition.hits.op}  #{condition.hits.data}


        ${condition.groupOp}   areaId  ${condition.areaId.op}  #{condition.areaId.data}


        ${condition.groupOp}  ( title like CONCAT('%', #{condition.keyword} , '%')
            OR summary LIKE CONCAT('%', #{condition.keyword} , '%')
            OR content LIKE CONCAT('%', #{condition.keyword} , '%')
        )




ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}
