UPDATE gk_user_account
status = #{status}, createDate = #{createDate}, account = #{account}, lastModDate = #{lastModDate}, password = #{password}, userType = #{userType},
WHERE id = #{id}

mapper.delete:
DELETE FROM gk_user_account


        AND id = #{id}


        AND status = #{status}


        AND createDate = #{createDate}


        AND account = #{account}


        AND lastModDate = #{lastModDate}


        AND password = #{password}


        AND userType = #{userType}

