mapper.select:
SELECT acc.id as id, acc.account as account, acc.password as password, info.icon as icon, exam.isReported as isReported, exam.isSurvey as isSurvey, info.name as name, acc.status as status, vip.status as vipStatus, info.subjectType as subjectType, info.sex as sex, info.schoolName as schoolName, info.provinceId as province, info.cityId as city, info.countyId as county, pro.code as userKey, vip.endDate, vip.activeDate, market.accountId, market.qrCodeUrl, market.sharerId, market.sharerType, market.agentLevel, card.productType as vipType, acc.isRegisterXueTang as isRegisterXueTang FROM zgk_user_account acc left join zgk_user_vip vip on acc.id = vip.id left join zgk_user_info info on acc.id = info.id left join zgk_user_exam exam on acc.id = exam.id left join zgk_province pro on pro.id = info.provinceId left join zgk_user_market market on acc.id=market.accountId LEFT JOIN zgk_card card on card.userId=acc.id
acc.userType = 0
and info.token = #{token}
and acc.id = #{id}
and acc.account = #{account}
and acc.areaId = #{areaId}
limit 1

SELECT acc.id, info.name, info.icon, acc.account, acc.password, acc.status, acc.userType, exam.isReported, exam.isSurvey, vip.status as vipStatus, pro.code as userKey, vip.endDate, vip.activeDate, market.accountId, market.qrCodeUrl, market.sharerId, market.sharerType, market.agentLevel FROM zgk_user_account acc left join zgk_user_vip vip on acc.id = vip.id left join zgk_user_info info on acc.id = info.id left join zgk_user_exam exam on acc.id = exam.id left join zgk_province pro on pro.id = info.provinceId left join zgk_user_market market on acc.id=market.accountId
acc.userType = 0
and info.token = #{token}
and acc.id = #{id}
and acc.account = #{account}
and acc.areaId = #{areaId}
limit 1

SELECT
  COUNT(acc.id)
FROM zgk_user_account acc
left join zgk_user_vip vip on acc.id = vip.id
left join zgk_user_info info on acc.id = info.id

    acc.userType = 0

        and info.token = #{token}


        and acc.id = #{id}


        and acc.account = #{account}


        and acc.areaId = #{areaId}

SELECT  user_id as id,phone as account,password,name,0 as vipStatus FROM zgk_user

    1=1

        and user_id = #{id}


        and phone = #{account}

select od.order_no as orderNo, od.product_price as amount, od.create_date as createDate, od.status as payStatus, od.goods_count as productNum, od.product_type as productType, od.unit_price as unitPrice, pro.productName as productName, od.handle_state as handleState, address.receivingAddress as receivingAddress, address.contactName as contactName, address.contactPhone as contactPhone from zgk_order od LEFT JOIN zgk_system.zgk_sale_product pro on od.product_type=pro.type LEFT JOIN zgk_user_goodsadress address on od.user_id=address.userId where od.user_id=#{userId} and od.state!='Y'
and od.order_no = #{orderNo}
order by od.create_date desc
