mapper.select:
SELECT  from zgk_product

    1=1

        AND type = #{type}


        AND code = #{code}



    limit #{startSize},#{endSize}

select pp.*
from zgk_product pp
inner join
(select DISTINCT eps.productId from zgk_expert_product_service eps
  where areaId=#{areaId}

   and eps.serviceId in (${specialitys})

) tmp on pp.id = tmp.productId
ORDER BY pp.price ASC

    limit ${offset}, ${rows}
