mapper.select:
SELECT
a.id,a.userId,a.question,a.createDate,a.disableExpertId,a.isAnswer,a.isOpen,a.freeStatus,
b.name userName,b.icon userIcon
FROM
shishuo_question a
left join shishuo_user_info b on a.userId = b.id

    a.status = 0

        AND a.freeStatus = #{freeStatus}


        AND a.expertId = #{expertId}


        AND a.isOpen = #{isOpen}


        AND a.isAnswer = #{isAnswer}


        AND a.sourceType = #{sourceType}


ORDER BY a.createDate desc

    limit #{startSize}, #{endSize}

SELECT
COUNT(a.ID)
FROM
shishuo_question a
left join shishuo_user_info b on a.userId = b.id

    a.status = 0

        AND a.freeStatus = #{freeStatus}


        AND a.expertId = #{expertId}


        AND a.isOpen = #{isOpen}


        AND a.isAnswer = #{isAnswer}


        AND a.sourceType = #{sourceType}

select a.id questionId,d.id userId,d.name userName,d.icon userIcon,a.question,a.createDate createTime,a.isAnswer,b.answer,b.createDate answerTime,a.disableStatus, c.id expertUserId,c.name expertUserName,c.icon expertUserIcon from shishuo_question a left join shishuo_answer b on a.id = b.questionId left join shishuo_user_expert c on b.expertId = c.id left join shishuo_user_info d on a.userId = d.id
1=1
AND a.id = #{questionId}
