mapper.select:
SELECT cc.id as id, cc.userId as userId, cc.projectId as universityId, cc.createDate as createDate, uu.name as universityName, (SELECT name FROM zgk_province pp WHERE uu.provinceCode=pp.id ) as provinceName, uu.type as universityType, uu.subjection as subjection, dd.name as propertyName, (select number from zgk_count where projectId = cc.projectId AND type='1' limit 1) as number FROM zgk_user_collect cc LEFT JOIN zgk_university uu ON cc.projectId = uu.id LEFT JOIN zgk_data_dict dd ON uu.property=dd.dictId AND dd.type='FEATURE'
1=1 AND cc.type = '1' AND cc.userId = #{userId}
ORDER BY cc.id desc
limit ${offset}, ${rows}

SELECT count(cc.id)
FROM zgk_user_collect cc

    1=1
    AND cc.userId = #{userId}
    AND cc.type = #{type}
