mapper.select:
select expert_id as expertId,
       vedio_name as vedioName,
       vedio_url as vedioUrl,
       create_date as createDate
from zgk_expert_vedio ev where ev.expert_id=#{expertId}
limit ${offset}, ${rows}

select expert_id as expertId, user_id as userId, user_name as userName, user_question as userQuestion, user_anwser as userAnwser, create_date as createDate from zgk_expert_user_question euq where euq.expert_id=#{expertId}
and euq.user_id=#{userId}
and euq.user_anwser != '' and euq.user_anwser is not null
limit ${offset}, ${rows}

select count(expert_id) as count
from zgk_expert_user_question euq
where euq.expert_id=#{expertId}

    and euq.user_id=#{userId}


    and euq.user_anwser != ''
    and euq.user_anwser is not null

select ea.expert_id as expertId, ea.user_id as userId, ea.user_name as userName, ea.user_comments as userComments, ea.is_checked as isChecked, ea.create_date as createDate, ei.expert_name as expertName, ec.config_value as serverTypeName from zgk_expert_appraise ea left join zgk_expert_info ei on ei.id=ea.expert_id left join zgk_expert_config ec on ec.config_domain='serviceType' and ec.config_key=ea.server_type where ea.is_checked=1
and ea.expert_id=#{expertId}
limit ${offset}, ${rows}

select ei.id as expertId,ei.expert_name as expertName,GROUP_CONCAT(DISTINCT ec1.config_value) as speciality , count(DISTINCT ec2.id) as casesNumber,GROUP_CONCAT(DISTINCT ec3.config_value) service,ei.qualification as qualification,expert_photo_url as photoUrl, ei.expert_intro as info from zgk_expert_info ei left join zgk_expert_speciality_relation esr on ei.id=esr.expert_id left join zgk_expert_config ec1 on ec1.config_domain='speciality' and ec1.config_key=esr.speciality_id left join zgk_expert_cases ec2 on ei.id=ec2.expert_id left join zgk_expert_service_relation esr2 on esr2.expert_id=ei.id left join zgk_expert_config ec3 on ec3.config_domain='serviceStyle' and ec3.config_key=esr2.service_style_id where ei.is_checked=1 and ei.id in
#{item}
group by ei.id
asc limit ${map.offset}, ${map.rows}

select count(ei.id) as count from zgk_expert_info ei left join zgk_expert_speciality_relation esr on ei.id=esr.expert_id left join zgk_expert_config ec1 on ec1.config_domain='speciality' and ec1.config_key=esr.speciality_id left join zgk_expert_cases ec2 on ei.id=ec2.expert_id left join zgk_expert_service_relation esr2 on esr2.expert_id=ei.id left join zgk_expert_config ec3 on ec3.config_domain='serviceStyle' and ec3.config_key=esr2.service_style_id where ei.is_checked=1 and ei.id in
#{item}
group by ei.id asc

select count(id) as count
from zgk_expert_config ec
where ec.config_domain=#{configDomain}

mapper.insert:
INSERT INTO zgk_expert_service_evaluation (
orderNo,
evaluation,
createDate
) VALUES (
#{orderNo},
#{evaluation},
#{createDate}
)

    SELECT LAST_INSERT_ID() AS ID
