select expert_id as expertId, user_id as userId, user_name as userName, server_type as serverType, user_comments as userComments, is_checked as isChecked, create_date as createDate from zgk_expert_appraise ea where ea.is_checked=1
and ea.expert_id=#{expertId}
limit ${offset}, ${rows}
