mapper.select:
select uu.id as universityId,zz.majorCode as majorCode,uu.provinceCode,zz.batch as batch,zz.universityName as universityName,zz.major as majorName,zz.batch,zz.planEnrolling as planNumber,zz.selectSubject as selectSubject


    ,IF(collection.createDate,1,0)  as isFavorite,collection.id as cid


    ,0 as isFavorite









where
(zz.selectSubject like CONCAT('%', #{subject1} , '%')
or zz.selectSubject like CONCAT('%', #{subject2} , '%')
or zz.selectSubject like CONCAT('%', #{subject3} , '%')
or zz.selectCount=0
)

    and zz.universityId = #{universityId}


    and zz.majorCode = #{majorId}


  and uu.provinceCode = #{provinceCode}


  and zz.batch = #{batch}


  and uu.type = #{type}

order by zz.universityId asc
limit ${offset},${rows}

select count(*)

where
  (zz.selectSubject like CONCAT('%', #{subject1} , '%')
    or zz.selectSubject like CONCAT('%', #{subject2} , '%')
    or zz.selectSubject like CONCAT('%', #{subject3} , '%')
    or zz.selectCount=0)

    and zz.universityId = #{universityId}


    and zz.majorCode = #{majorId}


    and uu.provinceCode = #{provinceCode}


    and zz.batch = #{batch}


    and uu.type = #{type}
