mapper.select:
select uu.id as universityId,zz.majorCode as majorCode,uu.provinceCode,tmp.batch as batch,zz.universityName as universityName, zz.major as majorName,tmp.batch,tmp.planEnrolling as planNumber,zz.selectSubject as selectSubject
,IF(collection.createDate,1,0) as isFavorite,collection.id as cid
,0 as isFavorite
from (select majorType,batch,universityId,majorCode,pp.planEnrolling from zgk_university_major_enrolling_plan pp where pp.areaId=330000 and pp.year=2016 ) tmp INNER join zgk_zj_3in7 zz on tmp.majorCode=zz.majorCode and tmp.universityId=zz.universityId INNER join zgk_university uu on uu.id=tmp.universityId
WHERE uu.id=#{universityId}
limit ${offset},${rows}
