mapper.select:
select uu.id as universityId,zz.majorCode as majorCode,uu.provinceCode,tmp.batch as batch,zz.universityName as universityName,zz.major as majorName,tmp.batch,tmp.planEnrolling as planNumber,zz.selectSubject as selectSubject
from (select batch,universityId,majorCode,pp.planEnrolling from zgk_university_major_enrolling_plan pp where pp.areaId=330000 and pp.year=2016 ) tmp
INNER join zgk_zj_3in7 zz on tmp.universityId=zz.universityId and tmp.majorCode=zz.majorCode
INNER join zgk_university uu on uu.id=tmp.universityId
WHERE uu.id=#{universityId}
limit ${offset},${rows}
